    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double f;
        double c;
        
        f=Double.parseDouble(jTextField1.getText());
        
        c=(f-32)/1.8;
        
        jTextField2.setText(String.valueOf(c));
        
        if(c < 7)
            {
                JOptionPane.showMessageDialog(null, "Temperatura Fria");
            }
        else if (c <= 20)
            
            {
                JOptionPane.showMessageDialog(null, "Temperatura Templada");
            }
        else
            {
                JOptionPane.showMessageDialog(null, "Temperatura calurosa");
            }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

