    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        double f;
        double c;
        
        c= Double.parseDouble(jTextField2.getText());
        f= c*1.8+32;
        
        jTextField1.setText(String.valueOf(f));
        
        if(f < 44.6)
            {
                JOptionPane.showMessageDialog(null, "Temperatura Fria");
            }
        else if (f <= 68)
            {
                JOptionPane.showMessageDialog(null, "Temperatura Templada");
            }
        else
            {
                JOptionPane.showMessageDialog(null, "Temperatura calurosa");
            }
        
        
    }//GEN-LAST:event_jButton4ActionPerformed

